    @ApiResponse(responseCode = "400", description = "Invalid table compaction request",
        content = @Content(
            mediaType = MediaType.TEXT_PLAIN,
            examples = @ExampleObject(
                value = "Invalid option combination: Can not use split-output here"
            )
        )
