@jwt.user_lookup_loader
def user_loader_callback(jwt_header: Any, jwt_payload: Dict[str, Any]) -> Any:
    import json

    # 从JWT payload中获取identity并解析
    identity_str = jwt_payload.get("sub")
    if not identity_str:
        raise UnAuthentication()  # type: ignore

    try:
