                    c.setPassword(editedConsumer.getPassword());
                }
                if(!c.getEmail().equals(editedConsumer.getEmail())) {
                    c.setEmail(editedConsumer.getEmail());
                }
                consumers.save(c);
                success = true;
            }
        }
        response.addProperty("success", success);
        return GSON.toJson(response);
